import { React, useRef } from "react";
import "../../Styles/Contact.css";
import Footer from "../Footer";
import emailjs from "emailjs-com";
require("dotenv").config();
function Contact() {
  const form = useRef();
  const handleSubmit = (e) => {
    e.preventDefault();
    emailjs
      .sendForm("gmail", "gmail_template", form.current, process.env.USER_ID)
      .then(
        (result) => {
          console.log(result.text);
        },
        (error) => {
          console.log(error.text);
        }
      );
  };
  return (
    <>
      <div className="Contact">
        <h1 data-aos="slide-left" className="contact-title">
          Contact Me
        </h1>
        <form data-aos="slide-right" onSubmit={handleSubmit}>
          <input
            id="name"
            name="name"
            type="text"
            placeholder="Name"
            minLength="3"
            maxLength="20"
            required
          />
          <textarea
            id="description"
            name="description"
            className="contact-description"
            placeholder="Description"
            maxLength="200"
            required
          />
          <input className="btn-grad" type="submit" value="Submit" />
        </form>
      </div>
      <Footer />
    </>
  );
}
export default Contact;
